// JavaScript source code
import React from 'react';
import './Post.css';
import ReactMarkdown from 'react-markdown';

function Post(props) {
    return (
        <article className="Post">
            <h3>{props.title}</h3>
            {/*<div>{props.body}</div>*/}
            <ReactMarkdown source={props.body } />
        </article>
        );
}

export default Post;
