// JavaScript source code
const initialState = {
    Title: '',
    //Body: '',
    Posts: [],
}

function PostsReducer(state = initialState, action) {
    if (action.type === 'CHANGE-TITLE') {

        console.log(state.Title);
        
        return {
            ...state,
            Title: action.value
        }
    }
    if (action.type === 'SUBMIT-POST') {
        console.log(state.Posts);

        const post = {
            Title: state.Title,
            Body: state.Body
        }
        var Posts = state.Posts.concat(post);
        return {
            ...state,
            Posts: Posts
        }
    }
    else {
        return state;
    }
}

export default PostsReducer;
