// JavaScript source code
import React, { Component } from 'react';
import './NewPost.css';
import { connect } from 'react-redux';

class NewPost extends Component {
    state = {
        Title: null,
        Body: null,
        posts: [],
    }

  

    render() {
       
        //console.log(this.props.title);
        return (
            <div>
                <form className="NewPost" onSubmit={this.props.onSubmitChangeHandler}>
                    <label>New Post</label>
                    <input type='text' placeholder='Title' onChange={this.props.onTitleChangeHandler } required />
                    <textarea rows='10' placeholder="Content" onChange={this.props.onBodyChangeHandler } required />
                    <button type='submit'>Post</button>
                </form>
            </div>
        );
    }
}

const mapStateToProps = state => {
    return {

        title: state.Title,
        //body: state.Body,
        //posts: state.Posts,

    };
};

const mapDispatchToProps = (dispatch) => {
    return {
        onTitleChangeHandler: (event) => { dispatch({ type: 'CHANGE-TITLE', value: event.target.value }) },
        onBodyChangeHandler: (event) => dispatch({ type: 'CHANGE-BODY', value: event.target.value }),
        onSubmitChangeHandler: (event) => { event.preventDefault(); dispatch({ type: 'SUBMIT-POST' }) }
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(NewPost);
