// JavaScript source code
import React, { Component } from 'react';
import { connect } from 'react-redux';
import Post from '../../Components/Post/Post';

class SearchedData extends Component {

    searchString = '';
    searchedData = [];
    componentDidMount() {
        //this.getSearchValue();
        this.searchString = this.props.match.params.data;
        //console.log(this.searchString);
        this.searchedData = this.props.posts.filter(ele => {
            if (Object.values(ele).includes(this.searchString))
                return ele;
        })

        //console.log(this.searchedData);
    }

    componentDidUpdate() {
        if (this.searchString !== this.props.match.params.data) {
            this.searchString = this.props.match.params.data;
            //console.log(this.searchString);
            this.searchedData = this.props.posts.filter(ele => {
                if (Object.values(ele).includes(this.searchString))
                    return ele;
            })
             //this.getSearchValue();

            console.log(this.searchedData);
        }
    }

    getSearchValue = () => {
        this.searchString = this.props.match.params.data;
        //console.log(this.searchString);
        this.searchedData = this.props.posts.filter(ele => {
            if (Object.values(ele).includes(this.searchString))
                return ele;
        })
        
    }

    render() {
        console.log(this.searchedData.length);
        const searchedPosts = this.searchedData.map(ele => {
            console.log(ele);
            return (<Post title={ele.Title} body={ele.Body} />);
        })
        return (
            <div>
                {searchedPosts}
            </div>
            );
    }
}
const mapStateToProps = (state) => {
    return {
        posts: state.Posts
    }
}
export default connect(mapStateToProps)(SearchedData);
