// JavaScript source code
import React, { Component } from 'react';
import './NewPost.css';
import { connect } from 'react-redux';

class NewPost extends Component {
    state = {
        Title: null,
        Body: null,
        posts: [],
    }

    //onTitleChangeHandler = (event) => {
    //    this.setState({ Title: event.target.value });
    //}

    //onBodyChangeHandler = (event) => {
    //    this.setState({ Body: event.target.value });
    //}

    //onSubmitChangeHandler = (event) => {
    //    event.preventDefault();
    //    console.log('Data Submitted');
    //    let post = {
    //        Title: this.state.Title,
    //        Body: this.state.Body,
    //    }
        
    //    this.setState({ posts: this.state.posts.concat(post) });

    //    console.log(this.state.posts);
    //}

    render() {
       
        console.log(this.props.title);
        return (
            <>
                <form className="NewPost" onSubmit={this.onSubmitChangeHandler}>
                    <label>New Post</label>
                    <input type='text' placeholder='Title' onChange={() =>  this.onTitleChangeHandler } required />
                    <textarea rows='10' placeholder="Content" onChange={this.onBodyChangeHandler } required />
                    <button type='submit'>Post</button>
                </form>
            </>
        );
    }
}

const mapStateToProps = state => {
    return {
        posts: state.posts,
        title: state.Title,
        body: state.Body

    };
}

const mapDispatchToProps = (event,dispatch) => {
    return {
        onTitleChangeHandler: (event) => { console.log(event); dispatch({ type: 'CHANGE-TITLE', value: event }) },
        onBodyChangeHandler: (event) => dispatch({ type: 'CHANGE-BODY', value: event }),
        onSubmitChangeHandler: (event) => { event.preventDefault(); dispatch({ type: 'SUBMIT-POST' }) }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(NewPost);
