// JavaScript source code
import React, { Component } from 'react';
import './NewPost.css';

class NewPost extends Component {
    state = {
        Title: null,
        Body: null,
        posts: [],
    }

    onTitleChangeHandler = (event) => {
        this.setState({ Title: event.target.value });
    }

    onBodyChangeHandler = (event) => {
        this.setState({ Body: event.target.value });
    }

    onSubmitChangeHandler = (event) => {
        event.preventDefault();
        console.log('Data Submitted');
        let post = {
            Title: this.state.Title,
            Body: this.state.Body,
        }
        
        this.setState({ posts: this.state.posts.concat(post) });

        console.log(this.state.posts);
    }

    render() {

        //console.log(this.state);
        return (
            <>
                <form className="NewPost" onSubmit={this.onSubmitChangeHandler}>
                    <label>New Post</label>
                    <input type='text' placeholder='Title' onChange={(event) => { this.onTitleChangeHandler(event) }} required />
                    <textarea rows='10' placeholder="Content" onChange={(event) => { this.onBodyChangeHandler(event) }} required />
                    <button type='submit'>Post</button>
                </form>
            </>
        );
    }
}

const mapStateToprops = state => {
    return {
        posts: state.posts
    };
}

const mapDispatchToProps = dispatch => {
    return {
        onTitleChangeHandler: (event) => dispatch({ type: 'CHANGE-TITLE', value: event.target.value }),
        onBodyChangeHandler: (event) => dispatch({ type: 'CHANGE-BODY', value: event.target.value }),
        onSubmitChangeHandler: () => dispatch({ type: 'SUBMIT-POST'})
    }
}

export default NewPost;
